import React, { useEffect, useState } from "react";
import "./home.css";
import windpic from "./images/wind.png";
import humiditypic from "./images/humidity.png";
import cloudpic from "./images/cloud.png";
import searchpic from "./images/search.png";
import axios from "axios";

function Home() {
  const[data,setData]= useState({
    celcius: 10,
    name: `Delhi`,
    humidity: 20,
    Speed: 20
  })
  useEffect(()=>{
    const apiUrl=`https://api.openweathermap.org/data/2.5/weather?q=ghaziabad&appid=a2d611690a4c930fcffe3c8715fe116e&&units=metric`
     axios.get(apiUrl)
     .then(res=> {
      console.log(res.data);
      setData({...data, celcius: res.data.main.temp,
        name: res.data.name, 
        humidity: res.data.main.humidity,
        Speed:res.data.wind.Speed})

     })
     .catch(err=>console.log(err));
  })
  return (
    <div className="container">
      <div className="weather">
        <div className="search">
          <input type="text" placeholder="Enter City name " />
          <button>
            <img src={searchpic} alt=""></img>
          </button>
        </div>
        <div className="winfo">
          <img src={cloudpic} alt="cloud" className="icon" />
        <h1>{Math.round(data.celcius)}Â°C</h1>
          <h2>{data.name}</h2>
          <div className="details">
            <div className="col">
              <div className="humidity">
              <img src={humiditypic} alt="humidity" />
                <p>Humidity</p>
                <p>{Math.round(data.humidity)}%</p>
              </div>
            </div>
            <div className="col">
              <div className="wind">
                <img src={windpic} alt="Wind" />
                <p>Wind</p>
                <p>{Math.round(data.Speed)}Km/h</p>
              </div>
            </div>
            <div className="col"></div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
